import React, { useState } from "react";
import axios from "axios";

export default function ReportCrime() {
  const [form, setForm] = useState({
    crimeType: "",
    description: "",
    latitude: "",
    longitude: "",
    timestamp: new Date().toISOString().slice(0, 16),
  });
  const [message, setMessage] = useState("");

  // Geolocation
  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((pos) => {
        setForm((f) => ({
          ...f,
          latitude: pos.coords.latitude,
          longitude: pos.coords.longitude,
        }));
      });
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/crime", {
        ...form,
        latitude: parseFloat(form.latitude),
        longitude: parseFloat(form.longitude),
      });
      setMessage("Incident reported successfully!");
      setForm({
        crimeType: "",
        description: "",
        latitude: "",
        longitude: "",
        timestamp: new Date().toISOString().slice(0, 16),
      });
    } catch {
      setMessage("Error reporting incident.");
    }
  };

  return (
    <div className="flex justify-center items-center min-h-[70vh] bg-gray-50">
      <div className="bg-white shadow rounded-lg p-8 w-full max-w-xl">
        <h2 className="text-2xl font-bold text-gray-900 mb-6 text-center">
          Report a Crime
        </h2>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Crime Type
            </label>
            <select
              name="crimeType"
              value={form.crimeType}
              onChange={handleChange}
              className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
              required
            >
              <option value="">Select crime type</option>
              <option value="Theft">Theft</option>
              <option value="Assault">Assault</option>
              <option value="Burglary">Burglary</option>
              <option value="Vandalism">Vandalism</option>
              <option value="Fraud">Fraud</option>
              <option value="Other">Other</option>
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Description
            </label>
            <textarea
              name="description"
              value={form.description}
              onChange={handleChange}
              rows={3}
              className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
              placeholder="Describe the incident (optional)"
            />
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Latitude
              </label>
              <input
                name="latitude"
                value={form.latitude}
                onChange={handleChange}
                type="number"
                step="any"
                className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="Latitude"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Longitude
              </label>
              <input
                name="longitude"
                value={form.longitude}
                onChange={handleChange}
                type="number"
                step="any"
                className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="Longitude"
                required
              />
            </div>
          </div>
          <button
            type="button"
            onClick={getLocation}
            className="bg-indigo-100 text-indigo-700 px-3 py-1 rounded mt-2 mb-2 hover:bg-indigo-200"
          >
            Use My Location
          </button>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Date & Time
            </label>
            <input
              name="timestamp"
              type="datetime-local"
              value={form.timestamp}
              onChange={handleChange}
              className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
              required
            />
          </div>
          <button
            type="submit"
            className="w-full bg-indigo-600 text-white py-2 px-4 rounded hover:bg-indigo-700 font-semibold"
          >
            Submit Report
          </button>
        </form>
        {message && (
          <div className="mt-6 text-center text-lg font-semibold text-indigo-700">
            {message}
          </div>
        )}
      </div>
    </div>
  );
}
