import React from "react";
import { useNavigate } from "react-router-dom";

const navOptions = [
  { label: "Crime Map", path: "/map", color: "from-indigo-500 to-blue-400" },
  { label: "Report Incident", path: "/report", color: "from-pink-500 to-red-400" },
  { label: "Emergency Contacts", path: "#contacts", color: "from-green-500 to-teal-400" },
];

export default function Home() {
  const navigate = useNavigate();

  return (
    <div className="flex flex-col items-center justify-center min-h-[70vh]">
      <h1 className="text-4xl font-bold mb-8 text-indigo-700 transition-all duration-700">Welcome to Namma Suraksha</h1>
      <div className="flex flex-wrap gap-8 justify-center">
        {navOptions.map((opt, i) => (
          <button
            key={opt.label}
            onClick={() => {
              if (opt.path === "#contacts") {
                document.getElementById("emergency-contacts-btn")?.click();
              } else {
                navigate(opt.path);
              }
            }}
            className={`bg-gradient-to-br ${opt.color} text-white px-8 py-6 rounded-2xl shadow-lg text-xl font-semibold transform transition-all duration-500 hover:scale-105 hover:shadow-2xl opacity-0 animate-fade-in`}
            style={{ animationDelay: `${i * 0.15 + 0.2}s`, animationFillMode: "forwards" }}
          >
            {opt.label}
          </button>
        ))}
      </div>
      <style>
        {`
          @keyframes fade-in {
            to { opacity: 1; }
          }
          .animate-fade-in {
            animation: fade-in 0.7s ease-in-out forwards;
          }
        `}
      </style>
    </div>
  );
}
