import React, { useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import {
  ShieldCheckIcon,
  MapIcon,
  ChartBarIcon,
  ExclamationTriangleIcon,
  PhoneIcon,
  ClipboardDocumentIcon,
} from "@heroicons/react/24/outline";
import { gsap } from "gsap";

function Navbar() {
  const navRef = useRef(null);
  const logoRef = useRef(null);
  const navItemsRef = useRef([]);

  useEffect(() => {
    // Navbar slide down animation
    gsap.from(navRef.current, {
      y: -100,
      duration: 1,
      ease: "power3.out",
    });

    // Logo fade in
    gsap.from(logoRef.current, {
      opacity: 0,
      x: -20,
      duration: 1,
      delay: 0.5,
    });

    // Nav items stagger animation
    gsap.from(navItemsRef.current, {
      opacity: 0,
      y: 20,
      duration: 0.5,
      stagger: 0.1,
      delay: 0.8,
    });
  }, []);

  const navItems = [
    { name: "Dashboard", path: "/", icon: ShieldCheckIcon },
    { name: "Crime Map", path: "/crime-map", icon: MapIcon },
    { name: "Crime Analytics", path: "/crime-analytics", icon: ChartBarIcon },
    {
      name: "Safety Tips",
      path: "/safety-tips",
      icon: ExclamationTriangleIcon,
    },
    {
      name: "Emergency Contacts",
      path: "/emergency-contacts",
      icon: PhoneIcon,
    },
    {
      name: "Report Incident",
      path: "/report-incident",
      icon: ClipboardDocumentIcon,
    },
  ];

  return (
    <nav ref={navRef} className="bg-white shadow-lg fixed w-full z-50">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between h-16">
          <div className="flex">
            <div className="flex-shrink-0 flex items-center">
              <span ref={logoRef} className="text-xl font-bold text-indigo-600">
                City Safety
              </span>
            </div>
            <div className="hidden sm:ml-6 sm:flex sm:space-x-8">
              {navItems.map((item, index) => (
                <Link
                  key={item.name}
                  to={item.path}
                  ref={(el) => (navItemsRef.current[index] = el)}
                  className="inline-flex items-center px-1 pt-1 text-sm font-medium text-gray-900 hover:text-indigo-600 transition-colors duration-200"
                >
                  <item.icon className="h-5 w-5 mr-1" />
                  {item.name}
                </Link>
              ))}
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
