import React from "react";
import { PhoneIcon, MapPinIcon, ClockIcon } from "@heroicons/react/24/outline";

function EmergencyContacts() {
  const emergencyCategories = [
    {
      title: "Police",
      contacts: [
        {
          name: "Emergency",
          number: "100",
          description: "24/7 Emergency Response",
        },
        {
          name: "Women Helpline",
          number: "1091",
          description: "24/7 Women Safety Helpline",
        },
        {
          name: "Traffic Police",
          number: "103",
          description: "Traffic Related Emergencies",
        },
      ],
    },
    {
      title: "Medical",
      contacts: [
        {
          name: "Ambulance",
          number: "102",
          description: "Medical Emergency Response",
        },
        {
          name: "COVID Helpline",
          number: "1075",
          description: "COVID-19 Related Assistance",
        },
        {
          name: "Poison Control",
          number: "1066",
          description: "Poison Information Center",
        },
      ],
    },
    {
      title: "Fire & Rescue",
      contacts: [
        {
          name: "Fire Brigade",
          number: "101",
          description: "Fire Emergency Response",
        },
        {
          name: "Disaster Management",
          number: "108",
          description: "Natural Disaster Response",
        },
        {
          name: "Mountain Rescue",
          number: "1092",
          description: "Mountain Emergency Response",
        },
      ],
    },
    {
      title: "Support Services",
      contacts: [
        {
          name: "Child Helpline",
          number: "1098",
          description: "Child Protection Services",
        },
        {
          name: "Senior Citizen",
          number: "1090",
          description: "Elderly Support Services",
        },
        {
          name: "Mental Health",
          number: "1800-599-0019",
          description: "Mental Health Support",
        },
      ],
    },
  ];

  const nearbyFacilities = [
    {
      name: "City Police Station",
      address: "123 Main Street, City Center",
      distance: "1.2 km",
      openHours: "24/7",
    },
    {
      name: "General Hospital",
      address: "456 Health Avenue, Medical District",
      distance: "2.5 km",
      openHours: "24/7",
    },
    {
      name: "Fire Station",
      address: "789 Safety Road, Industrial Area",
      distance: "3.1 km",
      openHours: "24/7",
    },
  ];

  return (
    <div className="space-y-6">
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">
          Emergency Contacts
        </h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {emergencyCategories.map((category) => (
            <div key={category.title} className="bg-gray-50 p-6 rounded-lg">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">
                {category.title}
              </h3>
              <div className="space-y-4">
                {category.contacts.map((contact) => (
                  <div key={contact.name} className="flex items-start">
                    <div className="flex-shrink-0">
                      <PhoneIcon className="h-6 w-6 text-indigo-600" />
                    </div>
                    <div className="ml-3">
                      <p className="text-sm font-medium text-gray-900">
                        {contact.name}
                      </p>
                      <p className="text-lg font-semibold text-indigo-600">
                        {contact.number}
                      </p>
                      <p className="text-sm text-gray-500">
                        {contact.description}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>

        <div className="mt-8">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            Nearby Emergency Facilities
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {nearbyFacilities.map((facility) => (
              <div key={facility.name} className="bg-indigo-50 p-4 rounded-lg">
                <h4 className="font-medium text-indigo-900">{facility.name}</h4>
                <div className="mt-2 space-y-2">
                  <div className="flex items-start">
                    <MapPinIcon className="h-5 w-5 text-indigo-600 mr-2" />
                    <span className="text-sm text-indigo-700">
                      {facility.address}
                    </span>
                  </div>
                  <div className="flex items-center">
                    <ClockIcon className="h-5 w-5 text-indigo-600 mr-2" />
                    <span className="text-sm text-indigo-700">
                      {facility.openHours}
                    </span>
                  </div>
                  <p className="text-sm text-indigo-700">
                    Distance: {facility.distance}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="mt-8 bg-red-50 p-6 rounded-lg">
          <h3 className="text-lg font-semibold text-red-900 mb-4">
            Emergency Response Guidelines
          </h3>
          <ul className="list-disc list-inside space-y-2 text-red-700">
            <li>Stay calm and speak clearly when calling emergency services</li>
            <li>Provide your exact location and the nature of the emergency</li>
            <li>Follow the instructions given by the emergency operator</li>
            <li>Keep the line open until help arrives</li>
            <li>
              If possible, send someone to guide emergency services to the
              location
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default EmergencyContacts;
