import React, { useState, useEffect } from "react";
import { gsap } from "gsap";
import { PhoneIcon, MapPinIcon } from "@heroicons/react/24/outline";

const SOSButton = () => {
  const [location, setLocation] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const buttonRef = React.useRef(null);

  useEffect(() => {
    // Get user's location when component mounts
    getCurrentLocation();
  }, []);

  const getCurrentLocation = () => {
    setIsLoading(true);
    setError(null);

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
            accuracy: position.coords.accuracy,
          });
          setIsLoading(false);
        },
        (error) => {
          setError("Unable to retrieve your location");
          setIsLoading(false);
          console.error("Error getting location:", error);
        },
        {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0,
        }
      );
    } else {
      setError("Geolocation is not supported by your browser");
      setIsLoading(false);
    }
  };

  const handleSOSClick = () => {
    if (!location) {
      getCurrentLocation();
      return;
    }

    // Animate button press
    gsap.to(buttonRef.current, {
      scale: 0.95,
      duration: 0.1,
      yoyo: true,
      repeat: 1,
    });

    // Send emergency alert
    sendEmergencyAlert();
  };

  const sendEmergencyAlert = () => {
    // Here you would typically send the location data to your backend
    // For now, we'll just show an alert
    const message = `Emergency Alert!\nLocation: ${location.latitude}, ${location.longitude}\nAccuracy: ${location.accuracy} meters`;
    alert(message);

    // You can also open the phone dialer with emergency number
    window.location.href = "tel:911";
  };

  return (
    <div className="fixed bottom-8 right-8 z-50">
      <button
        ref={buttonRef}
        onClick={handleSOSClick}
        className="bg-red-600 hover:bg-red-700 text-white rounded-full p-4 shadow-lg 
                 flex items-center justify-center transition-all duration-300
                 transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-opacity-50"
        style={{ width: "60px", height: "60px" }}
      >
        <PhoneIcon className="h-6 w-6" />
      </button>

      {/* Location status indicator */}
      <div className="absolute bottom-0 right-0 bg-white rounded-full p-1 shadow-md">
        {isLoading ? (
          <div className="w-3 h-3 bg-yellow-500 rounded-full animate-pulse" />
        ) : error ? (
          <div className="w-3 h-3 bg-red-500 rounded-full" />
        ) : (
          <div className="w-3 h-3 bg-green-500 rounded-full" />
        )}
      </div>

      {/* Location info tooltip */}
      {location && (
        <div className="absolute bottom-16 right-8 bg-white p-2 rounded-lg shadow-md text-sm">
          <div className="flex items-center">
            <MapPinIcon className="h-4 w-4 text-gray-500 mr-1" />
            <span>Location Ready</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default SOSButton;
