import React, { useRef } from "react";
import { MapPinIcon, UserIcon } from "@heroicons/react/24/outline";
import { useEmergency } from "../context/EmergencyContext";

const EmergencyMap = () => {
  const { location, emergencyHistory } = useEmergency();
  const mapRef = useRef(null);

  return (
    <div
      ref={mapRef}
      className="relative h-full w-full bg-gray-200 rounded-lg overflow-hidden"
    >
      {/* Map Container */}
      <div className="absolute inset-0 flex items-center justify-center">
        {/* Here you would integrate with a map service like Google Maps */}
        <div className="text-center">
          <MapPinIcon className="h-12 w-12 text-red-500 mx-auto mb-4" />
          <p className="text-gray-500">Map Integration Coming Soon</p>
        </div>
      </div>

      {/* Emergency Markers */}
      <div className="absolute inset-0 pointer-events-none">
        {emergencyHistory.map(
          (emergency, index) =>
            emergency.location && (
              <div
                key={index}
                className="emergency-marker absolute transform -translate-x-1/2 -translate-y-1/2"
                style={{
                  left: `${
                    ((emergency.location.longitude + 180) / 360) * 100
                  }%`,
                  top: `${((90 - emergency.location.latitude) / 180) * 100}%`,
                }}
              >
                <div className="relative">
                  <div className="absolute -inset-2 bg-red-500 rounded-full opacity-20 animate-ping" />
                  <div className="relative bg-red-500 text-white rounded-full p-2 shadow-lg">
                    <UserIcon className="h-4 w-4" />
                  </div>
                </div>
              </div>
            )
        )}
      </div>

      {/* Current Location Marker */}
      {location && (
        <div
          className="absolute transform -translate-x-1/2 -translate-y-1/2"
          style={{
            left: `${((location.longitude + 180) / 360) * 100}%`,
            top: `${((90 - location.latitude) / 180) * 100}%`,
          }}
        >
          <div className="relative">
            <div className="absolute -inset-2 bg-blue-500 rounded-full opacity-20 animate-ping" />
            <div className="relative bg-blue-500 text-white rounded-full p-2 shadow-lg">
              <MapPinIcon className="h-4 w-4" />
            </div>
          </div>
        </div>
      )}

      {/* Map Controls */}
      <div className="absolute bottom-4 right-4 flex space-x-2">
        <button className="bg-white p-2 rounded-full shadow-lg hover:bg-gray-100 transition-colors">
          <svg
            className="h-5 w-5 text-gray-600"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
            />
          </svg>
        </button>
        <button className="bg-white p-2 rounded-full shadow-lg hover:bg-gray-100 transition-colors">
          <svg
            className="h-5 w-5 text-gray-600"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M4 8V4m0 0h4M4 4l5 5m11-5v4m0-4h-4m4 4l-5 5M4 16v4m0 0h4m-4 0l5-5m11 5l-5-5m5 5v-4m0 4h-4"
            />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default EmergencyMap;
