import React, { useEffect, useState, useCallback } from "react";
import { GoogleMap, useJsApiLoader, Marker, InfoWindow } from "@react-google-maps/api";
import axios from "axios";
import { MapPinIcon, ExclamationTriangleIcon, ShieldCheckIcon } from "@heroicons/react/24/outline";

const mapContainerStyle = {
  width: "100%",
  height: "100%",
  borderRadius: "1rem",
};

const center = {
  lat: 12.9716,
  lng: 77.5946,
};

const options = {
  disableDefaultUI: false,
  zoomControl: true,
  mapTypeControl: true,
  streetViewControl: false,
  styles: [
    {
      featureType: "poi",
      elementType: "labels",
      stylers: [{ visibility: "off" }],
    },
  ],
};

export default function CrimeMap() {
  const [reports, setReports] = useState([]);
  const [filter, setFilter] = useState("");
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [map, setMap] = useState(null);

  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
  });

  const onLoad = useCallback(function callback(map) {
    setMap(map);
  }, []);

  const onUnmount = useCallback(function callback(map) {
    setMap(null);
  }, []);

  useEffect(() => {
    axios.get("http://localhost:5000/api/crime").then((res) => setReports(res.data));
  }, []);

  const filteredReports = filter
    ? reports.filter((r) => r.crimeType === filter)
    : reports;

  return (
    <div className="h-screen w-full flex items-center justify-center bg-gradient-to-br from-indigo-900 via-purple-800 to-pink-700 p-4 overflow-hidden">
      <div className="backdrop-blur-lg bg-white/70 rounded-3xl shadow-2xl p-4 w-full h-full max-w-[95vw] max-h-[95vh] flex flex-col">
        <h2 className="text-3xl font-extrabold text-indigo-900 mb-4 text-center drop-shadow flex items-center justify-center gap-2">
          <MapPinIcon className="h-7 w-7 text-pink-600" /> Crime Map
        </h2>
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 flex-1 min-h-0">
          <div className="lg:col-span-2 flex flex-col min-h-0">
            <select
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
              className="mb-2 border-2 border-indigo-400 p-2 rounded-lg shadow focus:ring-2 focus:ring-pink-400 transition"
            >
              <option value="">All Types</option>
              <option value="Theft">Theft</option>
              <option value="Assault">Assault</option>
              <option value="Burglary">Burglary</option>
              <option value="Vandalism">Vandalism</option>
              <option value="Fraud">Fraud</option>
              <option value="Other">Other</option>
            </select>
            <div className="rounded-2xl overflow-hidden shadow-lg border-2 border-indigo-200 animate-fade-in flex-1">
              {isLoaded ? (
                <GoogleMap
                  mapContainerStyle={mapContainerStyle}
                  zoom={12}
                  center={center}
                  options={options}
                  onLoad={onLoad}
                  onUnmount={onUnmount}
                >
                  {filteredReports.map((report) => (
                    <Marker
                      key={report.id}
                      position={{ lat: report.latitude, lng: report.longitude }}
                      onClick={() => setSelectedMarker(report)}
                      icon={{
                        url: `https://maps.google.com/mapfiles/ms/icons/${
                          report.crimeType === "Theft"
                            ? "yellow"
                            : report.crimeType === "Assault"
                            ? "red"
                            : report.crimeType === "Burglary"
                            ? "pink"
                            : report.crimeType === "Vandalism"
                            ? "blue"
                            : report.crimeType === "Fraud"
                            ? "green"
                            : "purple"
                        }-dot.png`
                      }}
                    />
                  ))}
                  {selectedMarker && (
                    <InfoWindow
                      position={{ lat: selectedMarker.latitude, lng: selectedMarker.longitude }}
                      onCloseClick={() => setSelectedMarker(null)}
                    >
                      <div className="p-2 min-w-[200px]">
                        <span className={`inline-block px-2 py-1 rounded-full text-xs font-bold mb-1 ${
                          selectedMarker.crimeType === "Theft"
                            ? "bg-yellow-200 text-yellow-800"
                            : selectedMarker.crimeType === "Assault"
                            ? "bg-red-200 text-red-800"
                            : selectedMarker.crimeType === "Burglary"
                            ? "bg-pink-200 text-pink-800"
                            : selectedMarker.crimeType === "Vandalism"
                            ? "bg-blue-200 text-blue-800"
                            : selectedMarker.crimeType === "Fraud"
                            ? "bg-green-200 text-green-800"
                            : "bg-gray-200 text-gray-800"
                        }`}>
                          {selectedMarker.crimeType}
                        </span>
                        <h3 className="font-bold mt-1">{selectedMarker.description}</h3>
                        <p className="text-xs text-gray-500 mt-1">
                          {new Date(selectedMarker.timestamp).toLocaleString()}
                        </p>
                      </div>
                    </InfoWindow>
                  )}
                </GoogleMap>
              ) : (
                <div className="w-full h-full flex items-center justify-center">
                  <div className="text-lg text-gray-600">Loading map...</div>
                </div>
              )}
            </div>
          </div>
          <div className="space-y-4 flex flex-col min-h-0 overflow-auto">
            <div className="bg-white/80 p-4 rounded-2xl shadow flex flex-col gap-2">
              <h3 className="text-lg font-bold text-indigo-700 mb-2 flex items-center gap-2">
                <ExclamationTriangleIcon className="h-5 w-5 text-red-500" /> Legend
              </h3>
              <div className="space-y-2">
                <div className="flex items-center gap-2">
                  <span className="w-3 h-3 bg-red-500 rounded-full inline-block"></span>
                  <span className="text-sm">High Risk</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-3 h-3 bg-orange-500 rounded-full inline-block"></span>
                  <span className="text-sm">Medium Risk</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-3 h-3 bg-green-500 rounded-full inline-block"></span>
                  <span className="text-sm">Low Risk</span>
                </div>
              </div>
            </div>
            <div className="bg-indigo-50/80 p-4 rounded-2xl shadow">
              <h3 className="text-lg font-bold text-indigo-900 mb-2 flex items-center gap-2">
                <ShieldCheckIcon className="h-5 w-5 text-green-500" /> Safety Tips
              </h3>
              <ul className="list-disc list-inside text-sm text-indigo-700 space-y-1">
                <li>Avoid isolated areas after dark</li>
                <li>Keep valuables secure and out of sight</li>
                <li>Stay in well-lit areas</li>
                <li>Be aware of your surroundings</li>
                <li>Report suspicious activities immediately</li>
              </ul>
            </div>
            <div className="bg-white/80 shadow rounded-2xl p-4 flex-1 overflow-auto">
              <h3 className="text-lg font-bold text-indigo-900 mb-2 flex items-center gap-2 sticky top-0 bg-white/80 py-2">
                <MapPinIcon className="h-5 w-5 text-pink-600" /> Reported Incidents
              </h3>
              <ul className="divide-y overflow-auto">
                {filteredReports.map((report) => (
                  <li key={report.id} className="py-2 flex flex-col gap-1">
                    <span className={`inline-block px-2 py-1 rounded-full text-xs font-bold ${
                      report.crimeType === "Theft"
                        ? "bg-yellow-200 text-yellow-800"
                        : report.crimeType === "Assault"
                        ? "bg-red-200 text-red-800"
                        : report.crimeType === "Burglary"
                        ? "bg-pink-200 text-pink-800"
                        : report.crimeType === "Vandalism"
                        ? "bg-blue-200 text-blue-800"
                        : report.crimeType === "Fraud"
                        ? "bg-green-200 text-green-800"
                        : "bg-gray-200 text-gray-800"
                    }`}>
                      {report.crimeType}
                    </span>
                    <span className="font-bold text-sm">{report.description}</span>
                    <span className="text-xs text-gray-500">
                      {new Date(report.timestamp).toLocaleString()}
                    </span>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
