import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import {
  MapIcon,
  ChartBarIcon,
  UserGroupIcon,
  ClockIcon,
  ShieldCheckIcon,
  ExclamationTriangleIcon,
  BellAlertIcon,
  PhoneIcon,
} from "@heroicons/react/24/outline";
import { useEmergency } from "../context/EmergencyContext";
import { useAuth } from "../context/AuthContext";

const EmergencyDashboard = () => {
  const { emergencyHistory, location } = useEmergency();
  const { user } = useAuth();
  const dashboardRef = useRef(null);

  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);

    // Animate dashboard elements on scroll
    const elements = dashboardRef.current.querySelectorAll(".dashboard-card");
    elements.forEach((element, index) => {
      gsap.from(element, {
        opacity: 0,
        y: 50,
        duration: 0.8,
        delay: index * 0.2,
        scrollTrigger: {
          trigger: element,
          start: "top 80%",
          toggleActions: "play none none reverse",
        },
      });
    });

    // Animation for emergency list
    gsap.from(".emergency-item", {
      duration: 0.6,
      x: -50,
      opacity: 0,
      stagger: 0.1,
      scrollTrigger: {
        trigger: ".emergency-list",
        start: "top center",
      },
    });
  }, []);

  return (
    <div
      ref={dashboardRef}
      className="min-h-screen bg-gradient-to-br from-indigo-50 to-purple-50"
    >
      <div className="max-w-7xl mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-indigo-900 mb-8">
          Emergency Dashboard
        </h1>

        {/* Stats Overview */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="dashboard-card bg-gradient-to-br from-blue-500 to-blue-600 p-6 rounded-lg shadow-lg text-white">
            <div className="flex items-center">
              <BellAlertIcon className="h-8 w-8 mr-4" />
              <div>
                <p className="text-sm font-medium">Total Emergencies</p>
                <p className="text-2xl font-bold">{emergencyHistory.length}</p>
              </div>
            </div>
          </div>

          <div className="dashboard-card bg-gradient-to-br from-red-500 to-red-600 p-6 rounded-lg shadow-lg text-white">
            <div className="flex items-center">
              <MapIcon className="h-8 w-8 mr-4" />
              <div>
                <p className="text-sm font-medium">Active Emergencies</p>
                <p className="text-2xl font-bold">
                  {emergencyHistory.filter((e) => e.status === "Active").length}
                </p>
              </div>
            </div>
          </div>

          <div className="dashboard-card bg-gradient-to-br from-green-500 to-green-600 p-6 rounded-lg shadow-lg text-white">
            <div className="flex items-center">
              <PhoneIcon className="h-8 w-8 mr-4" />
              <div>
                <p className="text-sm font-medium">Resolved Emergencies</p>
                <p className="text-2xl font-bold">
                  {
                    emergencyHistory.filter((e) => e.status === "Resolved")
                      .length
                  }
                </p>
              </div>
            </div>
          </div>

          <div className="dashboard-card bg-gradient-to-br from-purple-500 to-purple-600 p-6 rounded-lg shadow-lg text-white">
            <div className="flex items-center">
              <ChartBarIcon className="h-8 w-8 mr-4" />
              <div>
                <p className="text-sm font-medium">Avg Response Time</p>
                <p className="text-2xl font-bold">
                  {emergencyHistory.reduce(
                    (total, e) => total + e.responseTime,
                    0
                  ) / emergencyHistory.length}{" "}
                  minutes
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Emergency List */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-semibold text-indigo-900 mb-4">
            Recent Emergencies
          </h2>
          <div className="emergency-list space-y-4">
            {emergencyHistory.slice(-5).map((emergency) => (
              <div
                key={emergency.id}
                className="emergency-item bg-gradient-to-r from-indigo-50 to-purple-50 p-4 rounded-lg border border-indigo-100 hover:shadow-md transition-shadow"
              >
                <div className="flex items-center justify-between">
                  <div>
                    <h3 className="font-medium text-indigo-900">
                      {emergency.type}
                    </h3>
                    <p className="text-sm text-indigo-600">
                      {emergency.location}
                    </p>
                  </div>
                  <div className="flex items-center space-x-4">
                    <span
                      className={`px-3 py-1 rounded-full text-sm font-medium ${
                        emergency.status === "Active"
                          ? "bg-red-100 text-red-800"
                          : "bg-green-100 text-green-800"
                      }`}
                    >
                      {emergency.status}
                    </span>
                    <button className="text-indigo-600 hover:text-indigo-800">
                      <UserGroupIcon className="h-5 w-5" />
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmergencyDashboard;
