import React, { useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [form, setForm] = useState({ username: "", password: "" });
  const [message, setMessage] = useState("");
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/auth/login", form);
      login({ username: form.username });
      setMessage("Login successful!");
      setTimeout(() => {
        navigate("/");
      }, 500);
    } catch {
      setMessage("Invalid username or password.");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center relative overflow-hidden">
      {/* Animated Gradient Blobs */}
      <div className="absolute inset-0 z-0">
        <div className="absolute top-[-10%] left-[-10%] w-[60vw] h-[60vw] bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500 rounded-full filter blur-3xl opacity-60 animate-blob1"></div>
        <div className="absolute bottom-[-10%] right-[-10%] w-[60vw] h-[60vw] bg-gradient-to-tr from-pink-400 via-yellow-300 to-indigo-400 rounded-full filter blur-3xl opacity-50 animate-blob2"></div>
      </div>
      {/* Glassmorphism Card */}
      <form
        onSubmit={handleSubmit}
        className="relative z-10 bg-white/80 backdrop-blur-lg p-10 rounded-2xl shadow-2xl w-full max-w-md flex flex-col items-center"
      >
        <h2 className="text-3xl font-extrabold mb-6 text-center text-indigo-700 drop-shadow">Login</h2>
        <input
          name="username"
          placeholder="Username"
          value={form.username}
          onChange={handleChange}
          className="w-full border p-3 rounded mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-400 transition"
          required
        />
        <input
          name="password"
          type="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          className="w-full border p-3 rounded mb-6 focus:outline-none focus:ring-2 focus:ring-indigo-400 transition"
          required
        />
        <button
          type="submit"
          className="w-full bg-gradient-to-r from-indigo-600 to-pink-500 text-white py-3 rounded font-semibold shadow hover:from-indigo-700 hover:to-pink-600 transition-all duration-300"
        >
          Login
        </button>
        {message && <div className="text-center text-indigo-700 mt-4">{message}</div>}
        <div className="mt-6 w-full flex flex-col items-center">
          <span className="text-gray-600 mb-2">Don't have an account?</span>
          <button
            type="button"
            onClick={() => navigate("/register")}
            className="w-full bg-gradient-to-r from-pink-500 to-indigo-500 text-white py-2 rounded font-semibold shadow hover:from-pink-600 hover:to-indigo-600 transition-all duration-300"
          >
            Register
          </button>
        </div>
      </form>
      {/* Custom CSS for animated blobs */}
      <style>
        {`
          @keyframes blob1 {
            0%, 100% { transform: scale(1) translate(0, 0); }
            33% { transform: scale(1.1) translate(10vw, -5vw); }
            66% { transform: scale(0.9) translate(-5vw, 10vw); }
          }
          @keyframes blob2 {
            0%, 100% { transform: scale(1) translate(0, 0); }
            33% { transform: scale(1.1) translate(-10vw, 5vw); }
            66% { transform: scale(0.9) translate(5vw, -10vw); }
          }
          .animate-blob1 { animation: blob1 18s infinite ease-in-out; }
          .animate-blob2 { animation: blob2 22s infinite ease-in-out; }
        `}
      </style>
    </div>
  );
}
