import React, { useState, useEffect, useRef } from "react";
import { gsap } from "gsap";
import {
  PhoneIcon,
  MapPinIcon,
  ShieldExclamationIcon,
} from "@heroicons/react/24/outline";
import { useEmergency } from "../context/EmergencyContext";

const SOSButton = () => {
  const {
    emergencyContacts,
    isPanicMode,
    startPanicMode,
    stopPanicMode,
    location,
    setLocation,
    logEmergency,
  } = useEmergency();

  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [pressTimer, setPressTimer] = useState(null);
  const buttonRef = useRef(null);
  const panicModeRef = useRef(null);

  useEffect(() => {
    // Get initial location
    getCurrentLocation();
  }, []);

  const getCurrentLocation = () => {
    setIsLoading(true);
    setError(null);

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const newLocation = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
            accuracy: position.coords.accuracy,
          };
          setLocation(newLocation);
          setIsLoading(false);
        },
        (error) => {
          setError("Unable to retrieve your location");
          setIsLoading(false);
          console.error("Error getting location:", error);
        },
        {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0,
        }
      );
    } else {
      setError("Geolocation is not supported by your browser");
      setIsLoading(false);
    }
  };

  const handleMouseDown = () => {
    setPressTimer(
      setTimeout(() => {
        startPanicMode();
        gsap.to(buttonRef.current, {
          scale: 1.2,
          duration: 0.3,
          repeat: -1,
          yoyo: true,
        });
      }, 2000) // 2 seconds hold to activate panic mode
    );
  };

  const handleMouseUp = () => {
    if (pressTimer) {
      clearTimeout(pressTimer);
      setPressTimer(null);
    }

    if (!isPanicMode) {
      handleQuickPress();
    } else {
      stopPanicMode();
      gsap.killTweensOf(buttonRef.current);
      gsap.to(buttonRef.current, {
        scale: 1,
        duration: 0.3,
      });
    }
  };

  const handleQuickPress = () => {
    if (!location) {
      getCurrentLocation();
      return;
    }

    // Animate button press
    gsap.to(buttonRef.current, {
      scale: 0.95,
      duration: 0.1,
      yoyo: true,
      repeat: 1,
    });

    // Show emergency contacts modal
    showEmergencyContacts();
  };

  const showEmergencyContacts = () => {
    // Create and show emergency contacts modal
    const modal = document.createElement("div");
    modal.className =
      "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50";
    modal.innerHTML = `
      <div class="bg-white rounded-lg p-6 max-w-md w-full">
        <h3 class="text-xl font-bold mb-4">Emergency Contacts</h3>
        <div class="space-y-2">
          ${emergencyContacts
            .map(
              (contact) => `
            <div class="flex justify-between items-center p-2 hover:bg-gray-100 rounded">
              <span>${contact.name}</span>
              <a href="tel:${contact.number}" class="text-blue-600 hover:text-blue-800">
                ${contact.number}
              </a>
            </div>
          `
            )
            .join("")}
        </div>
        <button class="mt-4 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700">
          Close
        </button>
      </div>
    `;

    modal.querySelector("button").onclick = () => {
      document.body.removeChild(modal);
    };

    document.body.appendChild(modal);
  };

  const sendEmergencyAlert = () => {
    if (!location) return;

    const message = `Emergency Alert!\nLocation: ${location.latitude}, ${location.longitude}\nAccuracy: ${location.accuracy} meters`;
    logEmergency("sos", { message });
    alert(message);
  };

  return (
    <div className="fixed bottom-8 right-8 z-50">
      <button
        ref={buttonRef}
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
        onTouchStart={handleMouseDown}
        onTouchEnd={handleMouseUp}
        className={`${
          isPanicMode ? "bg-red-800" : "bg-red-600 hover:bg-red-700"
        } 
                   text-white rounded-full p-4 shadow-lg flex items-center justify-center 
                   transition-all duration-300 transform hover:scale-105 focus:outline-none 
                   focus:ring-2 focus:ring-red-500 focus:ring-opacity-50`}
        style={{ width: "60px", height: "60px" }}
      >
        {isPanicMode ? (
          <ShieldExclamationIcon className="h-6 w-6 animate-pulse" />
        ) : (
          <PhoneIcon className="h-6 w-6" />
        )}
      </button>

      {/* Location status indicator */}
      <div className="absolute bottom-0 right-0 bg-white rounded-full p-1 shadow-md">
        {isLoading ? (
          <div className="w-3 h-3 bg-yellow-500 rounded-full animate-pulse" />
        ) : error ? (
          <div className="w-3 h-3 bg-red-500 rounded-full" />
        ) : (
          <div className="w-3 h-3 bg-green-500 rounded-full" />
        )}
      </div>

      {/* Location info tooltip */}
      {location && (
        <div className="absolute bottom-16 right-8 bg-white p-2 rounded-lg shadow-md text-sm">
          <div className="flex items-center">
            <MapPinIcon className="h-4 w-4 text-gray-500 mr-1" />
            <span>{isPanicMode ? "Panic Mode Active" : "Location Ready"}</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default SOSButton;
