import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { EmergencyProvider } from "./context/EmergencyContext";
import "./index.css";
import Dashboard from "./pages/Dashboard";
import EmergencyMap from "./components/EmergencyMap";
import CrimeMap from "./pages/CrimeMap";
import SafetyTips from "./pages/SafetyTips";
import EmergencyContacts from "./pages/EmergencyContacts";
import ReportIncident from "./pages/ReportIncident";
import SOSButton from "./components/SOSButton";

function Navbar() {
  return (
    <nav style={{ padding: "1rem", background: "#4B0082", color: "#fff" }}>
      <Link to="/" style={{ color: "#fff", marginRight: 20 }}>Dashboard</Link>
      <Link to="/report-incident" style={{ color: "#fff" }}>Report Incident</Link>
    </nav>
  );
}

function App() {
  return (
    <Router>
      <EmergencyProvider>
        <div className="min-h-screen bg-gray-100 flex flex-col">
          <Navbar />
          <SOSButton />
          <main className="container mx-auto px-4 py-8 flex-1 w-full">
            <Routes>
              <Route path="/" element={<Dashboard />} />
              <Route path="/emergency-map" element={<EmergencyMap />} />
              <Route path="/crime-map" element={<CrimeMap />} />
              <Route path="/safety-tips" element={<SafetyTips />} />
              <Route
                path="/emergency-contacts"
                element={<EmergencyContacts />}
              />
              <Route path="/report-incident" element={<ReportIncident />} />
            </Routes>
          </main>
          <footer className="w-full text-center text-xs text-gray-400 pb-2 select-none">
            Made by the Ideashrooms
          </footer>
        </div>
      </EmergencyProvider>
    </Router>
  );
}

export default App;
