import React, { useState } from "react";
import axios from "axios";
import {
  MapPinIcon,
  CalendarIcon,
  ClockIcon,
  DocumentTextIcon,
} from "@heroicons/react/24/outline";
import { useAuth } from "../context/AuthContext";

export default function ReportCrime() {
  const { user } = useAuth();
  const [form, setForm] = useState({
    crimeType: "",
    description: "",
    latitude: "",
    longitude: "",
    contact: "",
    timestamp: new Date().toISOString().slice(0, 16),
  });
  const [message, setMessage] = useState("");

  // Geolocation
  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((pos) => {
        setForm((f) => ({
          ...f,
          latitude: pos.coords.latitude,
          longitude: pos.coords.longitude,
        }));
      });
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/crime", {
        ...form,
        reporter: user.username,
        latitude: parseFloat(form.latitude),
        longitude: parseFloat(form.longitude),
      });
      setMessage("Incident reported successfully!");
      setForm({
        crimeType: "",
        description: "",
        latitude: "",
        longitude: "",
        contact: "",
        timestamp: new Date().toISOString().slice(0, 16),
      });
    } catch {
      setMessage("Error reporting incident.");
    }
  };

  return (
    <div className="flex justify-center items-center min-h-[70vh] bg-gray-50">
      <div className="bg-white shadow rounded-lg p-8 w-full max-w-xl">
        <h2 className="text-2xl font-bold text-gray-900 mb-6 text-center">
          Report a Crime
        </h2>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label
                htmlFor="crimeType"
                className="block text-sm font-medium text-gray-700"
              >
                Crime Type
              </label>
              <select
                id="crimeType"
                name="crimeType"
                value={form.crimeType}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                required
              >
                <option value="">Select crime type</option>
                <option value="Theft">Theft</option>
                <option value="Assault">Assault</option>
                <option value="Burglary">Burglary</option>
                <option value="Vandalism">Vandalism</option>
                <option value="Fraud">Fraud</option>
                <option value="Other">Other</option>
              </select>
            </div>

            <div>
              <label
                htmlFor="latitude"
                className="block text-sm font-medium text-gray-700"
              >
                Latitude
              </label>
              <div className="mt-1 relative rounded-md shadow-sm">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <MapPinIcon className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  type="number"
                  name="latitude"
                  id="latitude"
                  value={form.latitude}
                  onChange={handleChange}
                  className="focus:ring-indigo-500 focus:border-indigo-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md"
                  placeholder="Enter latitude"
                  required
                  step="any"
                />
              </div>
            </div>

            <div>
              <label
                htmlFor="longitude"
                className="block text-sm font-medium text-gray-700"
              >
                Longitude
              </label>
              <div className="mt-1 relative rounded-md shadow-sm">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <MapPinIcon className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  type="number"
                  name="longitude"
                  id="longitude"
                  value={form.longitude}
                  onChange={handleChange}
                  className="focus:ring-indigo-500 focus:border-indigo-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md"
                  placeholder="Enter longitude"
                  required
                  step="any"
                />
              </div>
            </div>
          </div>

          <div>
            <label
              htmlFor="description"
              className="block text-sm font-medium text-gray-700"
            >
              Description
            </label>
            <div className="mt-1 relative rounded-md shadow-sm">
              <div className="absolute top-2 left-0 pl-3 pointer-events-none">
                <DocumentTextIcon className="h-5 w-5 text-gray-400" />
              </div>
              <textarea
                name="description"
                id="description"
                rows={4}
                value={form.description}
                onChange={handleChange}
                className="focus:ring-indigo-500 focus:border-indigo-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md"
                placeholder="Provide details about the incident"
                required
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Contact Number (optional)
            </label>
            <input
              name="contact"
              value={form.contact}
              onChange={handleChange}
              type="tel"
              className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
              placeholder="Your contact number (optional)"
            />
          </div>

          <div className="flex justify-end">
            <button
              type="submit"
              className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              Submit Report
            </button>
          </div>
        </form>
        {message && (
          <div className="mt-6 text-center text-lg font-semibold text-indigo-700">
            {message}
          </div>
        )}
      </div>
    </div>
  );
}
