import React, { useEffect, useState } from "react";
import axios from "axios";
import { MapPinIcon } from "@heroicons/react/24/outline";

export default function ReportedIncidents() {
  const [reports, setReports] = useState([]);
  const [filter, setFilter] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/api/crime").then((res) => setReports(res.data));
  }, []);

  const filteredReports = filter
    ? reports.filter((r) => r.crimeType === filter)
    : reports;

  return (
    <div className="w-screen h-screen flex items-center justify-center bg-gradient-to-br from-indigo-900 via-purple-800 to-pink-700">
      <div className="w-full max-w-4xl mx-auto p-4">
        <div className="bg-white/95 rounded-lg shadow-xl">
          <div className="flex items-center justify-between px-6 py-4 border-b border-indigo-100">
            <h2 className="text-xl font-semibold text-indigo-900 flex items-center gap-2">
              <MapPinIcon className="h-5 w-5 text-pink-600" /> Reported Incidents
            </h2>
            <select
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
              className="border border-indigo-300 bg-white px-3 py-1 rounded-lg text-sm focus:ring-2 focus:ring-pink-400 focus:border-transparent outline-none"
            >
              <option value="">All Types</option>
              <option value="Theft">Theft</option>
              <option value="Assault">Assault</option>
              <option value="Burglary">Burglary</option>
              <option value="Vandalism">Vandalism</option>
              <option value="Fraud">Fraud</option>
              <option value="Other">Other</option>
            </select>
          </div>
          
          <div className="p-6 grid gap-4">
            {filteredReports.map((report) => (
              <div key={report.id} className="bg-white rounded-lg shadow p-4 hover:shadow-md transition-shadow">
                <div className="flex items-start justify-between">
                  <div>
                    <span className={`inline-block px-2 py-1 rounded-full text-xs font-bold mb-2 ${
                      report.crimeType === "Theft"
                        ? "bg-yellow-100 text-yellow-800"
                        : report.crimeType === "Assault"
                        ? "bg-red-100 text-red-800"
                        : report.crimeType === "Burglary"
                        ? "bg-pink-100 text-pink-800"
                        : report.crimeType === "Vandalism"
                        ? "bg-blue-100 text-blue-800"
                        : report.crimeType === "Fraud"
                        ? "bg-green-100 text-green-800"
                        : "bg-gray-100 text-gray-800"
                    }`}>
                      {report.crimeType}
                    </span>
                    <p className="text-gray-700 font-medium mb-2">{report.description}</p>
                  </div>
                  <span className="text-xs text-gray-500">
                    {new Date(report.timestamp).toLocaleString()}
                  </span>
                </div>
                <div className="flex items-center gap-2 text-xs text-gray-500">
                  <MapPinIcon className="h-4 w-4" />
                  <span>Lat: {report.latitude.toFixed(4)}, Long: {report.longitude.toFixed(4)}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
