import React, { useState } from "react";
import axios from "axios";
import {
  MapPinIcon,
  CalendarIcon,
  ClockIcon,
  DocumentTextIcon,
  ExclamationTriangleIcon,
} from "@heroicons/react/24/outline";
import { useAuth } from "../context/AuthContext";

export default function ReportCrime() {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    crimeType: "",
    description: "",
    latitude: "",
    longitude: "",
    contactNumber: "",
    timestamp: new Date().toISOString().slice(0, 16),
  });
  const [message, setMessage] = useState("");
  const [isSuccess, setIsSuccess] = useState(false);

  // Geolocation
  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((pos) => {
        setFormData((f) => ({
          ...f,
          latitude: pos.coords.latitude,
          longitude: pos.coords.longitude,
        }));
      });
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/crime", {
        ...formData,
        reporter: user.username,
        latitude: parseFloat(formData.latitude),
        longitude: parseFloat(formData.longitude),
      });
      setIsSuccess(true);
      setMessage("Incident reported successfully!");
      setFormData({
        crimeType: "",
        description: "",
        latitude: "",
        longitude: "",
        contactNumber: "",
        timestamp: new Date().toISOString().slice(0, 16),
      });
    } catch (error) {
      setIsSuccess(false);
      setMessage("Error reporting incident. Please try again.");
    }
  };

  return (
    <div className="w-screen h-screen flex bg-gradient-to-br from-indigo-900 via-purple-800 to-pink-700">
      <div className="w-full h-full flex flex-col">
        <div className="flex items-center justify-between px-6 py-3 bg-indigo-100">
          <h2 className="text-xl font-semibold text-indigo-900 flex items-center gap-2">
            <ExclamationTriangleIcon className="h-5 w-5 text-pink-600" /> Report Incident
          </h2>
        </div>
        <div className="flex-1 flex items-center justify-center p-4">
          <form onSubmit={handleSubmit} className="w-full max-w-2xl bg-white/95 rounded-lg p-6">
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Type of Incident
                </label>
                <select
                  name="crimeType"
                  value={formData.crimeType}
                  onChange={handleChange}
                  required
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-400 focus:border-transparent"
                >
                  <option value="">Select type</option>
                  <option value="Theft">Theft</option>
                  <option value="Assault">Assault</option>
                  <option value="Burglary">Burglary</option>
                  <option value="Vandalism">Vandalism</option>
                  <option value="Fraud">Fraud</option>
                  <option value="Other">Other</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Description
                </label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  required
                  rows="4"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-400 focus:border-transparent"
                  placeholder="Please provide details about the incident..."
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Latitude
                  </label>
                  <div className="relative">
                    <MapPinIcon className="h-5 w-5 text-gray-400 absolute left-3 top-2.5" />
                    <input
                      type="number"
                      step="any"
                      name="latitude"
                      value={formData.latitude}
                      onChange={handleChange}
                      required
                      className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-400 focus:border-transparent"
                      placeholder="Enter latitude"
                    />
                  </div>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Longitude
                  </label>
                  <div className="relative">
                    <MapPinIcon className="h-5 w-5 text-gray-400 absolute left-3 top-2.5" />
                    <input
                      type="number"
                      step="any"
                      name="longitude"
                      value={formData.longitude}
                      onChange={handleChange}
                      required
                      className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-400 focus:border-transparent"
                      placeholder="Enter longitude"
                    />
                  </div>
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Contact Number (Optional)
                </label>
                <input
                  type="tel"
                  name="contactNumber"
                  value={formData.contactNumber}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-400 focus:border-transparent"
                  placeholder="Your contact number"
                />
              </div>

              <button
                type="submit"
                className="w-full bg-gradient-to-r from-pink-600 to-indigo-600 text-white py-2 px-4 rounded-lg font-medium hover:from-pink-700 hover:to-indigo-700 focus:ring-2 focus:ring-offset-2 focus:ring-pink-500 transition-all duration-300"
              >
                Submit Report
              </button>

              {message && (
                <div
                  className={`mt-4 p-3 rounded-lg ${
                    isSuccess ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                  }`}
                >
                  {message}
                </div>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
