import React, { useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [form, setForm] = useState({ username: "", password: "" });
  const [message, setMessage] = useState("");
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/auth/login", form);
      login({ username: form.username });
      setMessage("Login successful!");
      setTimeout(() => {
        navigate("/");
      }, 500);
    } catch {
      setMessage("Invalid username or password.");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-400 via-purple-300 to-yellow-200"
      style={{
        backgroundImage: "url('https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=1500&q=80')",
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <form
        onSubmit={handleSubmit}
        className="bg-white/90 backdrop-blur-lg p-10 rounded-3xl shadow-2xl w-full max-w-md flex flex-col items-center border border-gray-200"
        style={{
          boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.37), 0 1.5px 6px 0 rgba(0,0,0,0.15)",
          border: "1px solid rgba(255,255,255,0.18)",
        }}
      >
        <h2 className="text-3xl font-extrabold mb-6 text-center text-indigo-700 drop-shadow">Login</h2>
        <input
          name="username"
          placeholder="Username"
          value={form.username}
          onChange={handleChange}
          className="w-full border p-3 rounded mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-400 transition"
          required
        />
        <input
          name="password"
          type="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          className="w-full border p-3 rounded mb-6 focus:outline-none focus:ring-2 focus:ring-indigo-400 transition"
          required
        />
        <button
          type="submit"
          className="w-full bg-gradient-to-r from-indigo-600 to-pink-500 text-white py-3 rounded font-semibold shadow hover:from-indigo-700 hover:to-pink-600 transition-all duration-300"
        >
          Login
        </button>
        {message && <div className="text-center text-indigo-700 mt-4">{message}</div>}
        <div className="mt-6 w-full flex flex-col items-center">
          <span className="text-gray-600 mb-2">Don't have an account?</span>
          <button
            type="button"
            onClick={() => navigate("/register")}
            className="w-full bg-gradient-to-r from-pink-500 to-indigo-500 text-white py-2 rounded font-semibold shadow hover:from-pink-600 hover:to-indigo-600 transition-all duration-300"
          >
            Register
          </button>
        </div>
      </form>
    </div>
  );
}
