import React, { useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Fix for default marker icons in Leaflet with React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
  iconUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
  shadowUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
});

function CrimeMap() {
  const [selectedArea, setSelectedArea] = useState(null);

  // Sample crime data
  const crimeData = [
    {
      id: 1,
      location: [28.6139, 77.209], // Delhi coordinates
      type: "Theft",
      severity: "High",
      description: "Multiple theft incidents reported in the area",
      timestamp: "2024-04-28 14:30",
    },
    {
      id: 2,
      location: [28.7041, 77.1025],
      type: "Assault",
      severity: "Medium",
      description: "Physical altercation reported",
      timestamp: "2024-04-28 15:45",
    },
    {
      id: 3,
      location: [28.5355, 77.391],
      type: "Burglary",
      severity: "High",
      description: "Residential break-in reported",
      timestamp: "2024-04-28 16:20",
    },
  ];

  const getMarkerColor = (severity) => {
    switch (severity.toLowerCase()) {
      case "high":
        return "red";
      case "medium":
        return "orange";
      case "low":
        return "green";
      default:
        return "blue";
    }
  };

  return (
    <div className="space-y-6">
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-4">Crime Map</h2>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          <div className="lg:col-span-2 h-[600px]">
            <MapContainer
              center={[28.6139, 77.209]}
              zoom={12}
              style={{ height: "100%", width: "100%" }}
            >
              <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              />
              {crimeData.map((crime) => (
                <Marker
                  key={crime.id}
                  position={crime.location}
                  eventHandlers={{
                    click: () => setSelectedArea(crime),
                  }}
                >
                  <Popup>
                    <div className="p-2">
                      <h3 className="font-bold">{crime.type}</h3>
                      <p className="text-sm">{crime.description}</p>
                      <p className="text-xs text-gray-500">{crime.timestamp}</p>
                    </div>
                  </Popup>
                </Marker>
              ))}
            </MapContainer>
          </div>

          <div className="space-y-4">
            <div className="bg-gray-50 p-4 rounded-lg">
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                Legend
              </h3>
              <div className="space-y-2">
                <div className="flex items-center">
                  <div className="w-4 h-4 bg-red-500 rounded-full mr-2"></div>
                  <span className="text-sm">High Risk</span>
                </div>
                <div className="flex items-center">
                  <div className="w-4 h-4 bg-orange-500 rounded-full mr-2"></div>
                  <span className="text-sm">Medium Risk</span>
                </div>
                <div className="flex items-center">
                  <div className="w-4 h-4 bg-green-500 rounded-full mr-2"></div>
                  <span className="text-sm">Low Risk</span>
                </div>
              </div>
            </div>

            {selectedArea && (
              <div className="bg-white p-4 rounded-lg shadow">
                <h3 className="text-lg font-semibold text-gray-900 mb-2">
                  Area Details
                </h3>
                <div className="space-y-2">
                  <p>
                    <span className="font-medium">Type:</span>{" "}
                    {selectedArea.type}
                  </p>
                  <p>
                    <span className="font-medium">Severity:</span>{" "}
                    {selectedArea.severity}
                  </p>
                  <p>
                    <span className="font-medium">Description:</span>{" "}
                    {selectedArea.description}
                  </p>
                  <p>
                    <span className="font-medium">Time:</span>{" "}
                    {selectedArea.timestamp}
                  </p>
                </div>
              </div>
            )}

            <div className="bg-indigo-50 p-4 rounded-lg">
              <h3 className="text-lg font-semibold text-indigo-900 mb-2">
                Safety Tips
              </h3>
              <ul className="list-disc list-inside text-sm text-indigo-700 space-y-1">
                <li>Avoid isolated areas after dark</li>
                <li>Keep valuables secure and out of sight</li>
                <li>Stay in well-lit areas</li>
                <li>Be aware of your surroundings</li>
                <li>Report suspicious activities immediately</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CrimeMap;
