import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import TransitionOverlay from "../components/TransitionOverlay";

const navOptions = [
  {
    label: "üó∫Ô∏è Crime Map",
    path: "/map",
    color: "from-blue-700 to-indigo-500",
    desc: "See recent crime reports in your area on an interactive map."
  },
  {
    label: "üìù Report Incident",
    path: "/report",
    color: "from-pink-600 to-red-400",
    desc: "Help your community by reporting a crime or suspicious activity."
  },
  {
    label: "üö® Emergency Contacts",
    path: "#contacts",
    color: "from-green-500 to-teal-400",
    desc: "Quickly access police, ambulance, and other helplines."
  }
];

export default function Home() {
  const navigate = useNavigate();
  const [showTransition, setShowTransition] = useState(false);
  const [transitionMsg, setTransitionMsg] = useState("");

  const handleNavigateWithTransition = (path, msg) => {
    setTransitionMsg(msg);
    setShowTransition(true);
    setTimeout(() => {
      setShowTransition(false);
      if (path === "#contacts") {
        document.getElementById("emergency-contacts-btn")?.click();
      } else {
        navigate(path);
      }
    }, 3000);
  };

  return (
    <>
      <TransitionOverlay show={showTransition} message={transitionMsg} />
      <div className="flex flex-col items-center justify-center w-full h-full min-h-screen min-w-full">
        <h1 className="text-5xl font-extrabold mb-8 text-white drop-shadow-lg text-center">
          üëÆ‚Äç‚ôÇÔ∏è Welcome to Namma Suraksha
        </h1>
        <div className="flex flex-wrap gap-10 justify-center mb-12">
          {navOptions.map((opt, i) => (
            <div
              key={opt.label}
              className={`flex flex-col items-center bg-gradient-to-br ${opt.color} text-white px-10 py-8 rounded-3xl shadow-2xl text-2xl font-bold transform transition-all duration-500 hover:scale-105 hover:shadow-3xl border-2 border-white/20`}
              style={{
                boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.37), 0 1.5px 6px 0 rgba(0,0,0,0.15)",
                minWidth: "270px",
                maxWidth: "320px",
                cursor: "pointer"
              }}
              onClick={() =>
                handleNavigateWithTransition(
                  opt.path,
                  `Opening ${opt.label.replace(/^[^a-zA-Z]+/, "")}...`
                )
              }
            >
              <span className="mb-2">{opt.label}</span>
              <span className="text-base font-normal text-white/90 text-center mt-2">{opt.desc}</span>
            </div>
          ))}
        </div>
        <div className="mt-6 text-white text-lg font-semibold bg-black/50 px-8 py-4 rounded-2xl shadow-lg flex flex-col items-center">
          <span>üåÉ Your safety, our priority.</span>
          <span>‚ú® Stay alert. Stay safe. Empower your community.</span>
        </div>
      </div>
    </>
  );
}
