import React, { useEffect, useState } from "react";
import { GoogleMap, LoadScript, MarkerF, InfoWindowF } from "@react-google-maps/api";
import axios from "axios";
import { MapPinIcon, ExclamationTriangleIcon, ShieldCheckIcon } from "@heroicons/react/24/outline";

const mapContainerStyle = {
  width: "100%",
  height: "100%",
  borderRadius: "1rem",
};

const center = {
  lat: 12.9716,
  lng: 77.5946,
};

const options = {
  disableDefaultUI: false,
  zoomControl: true,
  mapTypeControl: true,
  streetViewControl: false,
  styles: [
    {
      featureType: "poi",
      elementType: "labels",
      stylers: [{ visibility: "off" }],
    },
  ],
};

export default function CrimeMap() {
  const [reports, setReports] = useState([]);
  const [filter, setFilter] = useState("");
  const [selectedMarker, setSelectedMarker] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:5000/api/crime").then((res) => setReports(res.data));
  }, []);

  const filteredReports = filter
    ? reports.filter((r) => r.crimeType === filter)
    : reports;

  return (
    <div className="min-h-screen w-full flex flex-col items-center justify-center bg-gradient-to-br from-indigo-900 via-purple-800 to-pink-700 py-10 px-2">
      <div className="backdrop-blur-lg bg-white/70 rounded-3xl shadow-2xl p-8 w-full max-w-6xl animate-fade-in">
        <h2 className="text-4xl font-extrabold text-indigo-900 mb-8 text-center drop-shadow flex items-center justify-center gap-2">
          <MapPinIcon className="h-8 w-8 text-pink-600" /> Crime Map
        </h2>
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          <div className="lg:col-span-2 h-[600px]">
            <select
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
              className="mb-4 border-2 border-indigo-400 p-2 rounded-lg shadow focus:ring-2 focus:ring-pink-400 transition"
            >
              <option value="">All Types</option>
              <option value="Theft">Theft</option>
              <option value="Assault">Assault</option>
              <option value="Burglary">Burglary</option>
              <option value="Vandalism">Vandalism</option>
              <option value="Fraud">Fraud</option>
              <option value="Other">Other</option>
            </select>
            <div className="rounded-2xl overflow-hidden shadow-lg border-2 border-indigo-200 animate-fade-in h-full">
              <LoadScript googleMapsApiKey="AIzaSyADQBOUNz-ZgxBckWD4gfFudpYjcG5PhxQ">
                <GoogleMap
                  mapContainerStyle={mapContainerStyle}
                  zoom={12}
                  center={center}
                  options={options}
                >
                  {filteredReports.map((report) => (
                    <MarkerF
                      key={report.id}
                      position={{ lat: report.latitude, lng: report.longitude }}
                      onClick={() => setSelectedMarker(report)}
                      icon={{
                        url: `https://maps.google.com/mapfiles/ms/icons/${
                          report.crimeType === "Theft"
                            ? "yellow"
                            : report.crimeType === "Assault"
                            ? "red"
                            : report.crimeType === "Burglary"
                            ? "pink"
                            : report.crimeType === "Vandalism"
                            ? "blue"
                            : report.crimeType === "Fraud"
                            ? "green"
                            : "purple"
                        }-dot.png`
                      }}
                    />
                  ))}
                  {selectedMarker && (
                    <InfoWindowF
                      position={{ lat: selectedMarker.latitude, lng: selectedMarker.longitude }}
                      onCloseClick={() => setSelectedMarker(null)}
                    >
                      <div className="p-2 min-w-[200px]">
                        <span className={`inline-block px-2 py-1 rounded-full text-xs font-bold mb-1 ${
                          selectedMarker.crimeType === "Theft"
                            ? "bg-yellow-200 text-yellow-800"
                            : selectedMarker.crimeType === "Assault"
                            ? "bg-red-200 text-red-800"
                            : selectedMarker.crimeType === "Burglary"
                            ? "bg-pink-200 text-pink-800"
                            : selectedMarker.crimeType === "Vandalism"
                            ? "bg-blue-200 text-blue-800"
                            : selectedMarker.crimeType === "Fraud"
                            ? "bg-green-200 text-green-800"
                            : "bg-gray-200 text-gray-800"
                        }`}>
                          {selectedMarker.crimeType}
                        </span>
                        <h3 className="font-bold mt-1">{selectedMarker.description}</h3>
                        <p className="text-xs text-gray-500 mt-1">
                          {new Date(selectedMarker.timestamp).toLocaleString()}
                        </p>
                      </div>
                    </InfoWindowF>
                  )}
                </GoogleMap>
              </LoadScript>
            </div>
          </div>
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white/80 p-5 rounded-2xl shadow flex flex-col gap-3">
              <h3 className="text-xl font-bold text-indigo-700 mb-2 flex items-center gap-2">
                <ExclamationTriangleIcon className="h-6 w-6 text-red-500" /> Legend
              </h3>
              <div className="space-y-2">
                <div className="flex items-center gap-2">
                  <span className="w-4 h-4 bg-red-500 rounded-full inline-block"></span>
                  <span className="text-sm">High Risk</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-4 h-4 bg-orange-500 rounded-full inline-block"></span>
                  <span className="text-sm">Medium Risk</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-4 h-4 bg-green-500 rounded-full inline-block"></span>
                  <span className="text-sm">Low Risk</span>
                </div>
              </div>
            </div>
            <div className="bg-indigo-50/80 p-5 rounded-2xl shadow">
              <h3 className="text-xl font-bold text-indigo-900 mb-2 flex items-center gap-2">
                <ShieldCheckIcon className="h-6 w-6 text-green-500" /> Safety Tips
              </h3>
              <ul className="list-disc list-inside text-sm text-indigo-700 space-y-1">
                <li>Avoid isolated areas after dark</li>
                <li>Keep valuables secure and out of sight</li>
                <li>Stay in well-lit areas</li>
                <li>Be aware of your surroundings</li>
                <li>Report suspicious activities immediately</li>
              </ul>
            </div>
          </div>
        </div>
        <div className="bg-white/80 shadow rounded-2xl p-6 mt-8 animate-fade-in">
          <h3 className="text-2xl font-bold mb-4 text-indigo-900 flex items-center gap-2">
            <MapPinIcon className="h-6 w-6 text-pink-600" /> Reported Incidents
          </h3>
          <ul className="divide-y">
            {filteredReports.map((report) => (
              <li key={report.id} className="py-3 flex flex-col md:flex-row md:items-center md:justify-between gap-2">
                <span className={`inline-block px-2 py-1 rounded-full text-xs font-bold mr-2 ${
                  report.crimeType === "Theft"
                    ? "bg-yellow-200 text-yellow-800"
                    : report.crimeType === "Assault"
                    ? "bg-red-200 text-red-800"
                    : report.crimeType === "Burglary"
                    ? "bg-pink-200 text-pink-800"
                    : report.crimeType === "Vandalism"
                    ? "bg-blue-200 text-blue-800"
                    : report.crimeType === "Fraud"
                    ? "bg-green-200 text-green-800"
                    : "bg-gray-200 text-gray-800"
                }`}>
                  {report.crimeType}
                </span>
                <span className="font-bold">{report.description}</span>
                <span className="text-xs text-gray-500">
                  {new Date(report.timestamp).toLocaleString()} | Lat: {report.latitude}, Lng: {report.longitude}
                </span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
