import React from "react";
import { useNavigate } from "react-router-dom";

const navOptions = [
  { label: "Crime Map", path: "/map", color: "from-indigo-500 to-blue-400" },
  { label: "Report Incident", path: "/report", color: "from-pink-500 to-red-400" },
  { label: "Emergency Contacts", path: "#contacts", color: "from-green-500 to-teal-400" },
];

export default function Home() {
  const navigate = useNavigate();

  return (
    <div
      className="flex flex-col items-center justify-center min-h-[70vh] bg-gradient-to-br from-indigo-400 via-purple-300 to-yellow-200"
      style={{
        backgroundImage: "url('https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=1500&q=80')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
      }}
    >
      <h1 className="text-5xl font-extrabold mb-8 text-white drop-shadow-lg">Welcome to Namma Suraksha</h1>
      <div className="flex flex-wrap gap-8 justify-center">
        {navOptions.map((opt, i) => (
          <button
            key={opt.label}
            onClick={() => {
              if (opt.path === "#contacts") {
                document.getElementById("emergency-contacts-btn")?.click();
              } else {
                navigate(opt.path);
              }
            }}
            className={`bg-gradient-to-br ${opt.color} text-white px-10 py-8 rounded-3xl shadow-2xl text-2xl font-bold transform transition-all duration-500 hover:scale-105 hover:shadow-3xl border-2 border-white/30`}
            style={{
              boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.37), 0 1.5px 6px 0 rgba(0,0,0,0.15)",
            }}
          >
            {opt.label}
          </button>
        ))}
      </div>
      <div className="mt-10 text-white text-lg font-semibold bg-black/40 px-6 py-3 rounded-2xl shadow-lg">
        <span>Stay safe. Report incidents. Help your community.</span>
      </div>
    </div>
  );
}
