import React, { useEffect, useState, useCallback } from "react";
import { GoogleMap, useJsApiLoader, Marker, InfoWindow } from "@react-google-maps/api";
import axios from "axios";
import { MapPinIcon, ExclamationTriangleIcon, ShieldCheckIcon } from "@heroicons/react/24/outline";

const mapContainerStyle = {
  width: "100%",
  height: "100%",
  borderRadius: "0.5rem",
};

const center = {
  lat: 12.9716,
  lng: 77.5946,
};

const options = {
  disableDefaultUI: false,
  zoomControl: true,
  mapTypeControl: true,
  streetViewControl: false,
  styles: [
    {
      featureType: "poi",
      elementType: "labels",
      stylers: [{ visibility: "off" }],
    },
  ],
};

export default function CrimeMap() {
  const [reports, setReports] = useState([]);
  const [filter, setFilter] = useState("");
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [map, setMap] = useState(null);

  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
  });

  const onLoad = useCallback(function callback(map) {
    setMap(map);
  }, []);

  const onUnmount = useCallback(function callback(map) {
    setMap(null);
  }, []);

  useEffect(() => {
    axios.get("http://localhost:5000/api/crime").then((res) => setReports(res.data));
  }, []);

  const filteredReports = filter
    ? reports.filter((r) => r.crimeType === filter)
    : reports;

  return (
    <div className="h-screen w-full flex items-stretch bg-gradient-to-br from-indigo-900 via-purple-800 to-pink-700 p-2">
      <div className="bg-white/90 backdrop-blur-sm rounded-2xl shadow-xl w-full flex flex-col overflow-hidden">
        <div className="flex items-center justify-between px-4 py-2 bg-indigo-100/80">
          <h2 className="text-xl font-semibold text-indigo-900 flex items-center gap-2">
            <MapPinIcon className="h-5 w-5 text-pink-600" /> Crime Map
          </h2>
          <select
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            className="border border-indigo-300 bg-white px-3 py-1 rounded-lg text-sm focus:ring-2 focus:ring-pink-400 focus:border-transparent outline-none"
          >
            <option value="">All Types</option>
            <option value="Theft">Theft</option>
            <option value="Assault">Assault</option>
            <option value="Burglary">Burglary</option>
            <option value="Vandalism">Vandalism</option>
            <option value="Fraud">Fraud</option>
            <option value="Other">Other</option>
          </select>
        </div>
        <div className="flex flex-1 min-h-0">
          <div className="flex-1 p-2">
            <div className="w-full h-full rounded-lg overflow-hidden border border-indigo-200 shadow-inner">
              {isLoaded ? (
                <GoogleMap
                  mapContainerStyle={mapContainerStyle}
                  zoom={12}
                  center={center}
                  options={options}
                  onLoad={onLoad}
                  onUnmount={onUnmount}
                >
                  {filteredReports.map((report) => (
                    <Marker
                      key={report.id}
                      position={{ lat: report.latitude, lng: report.longitude }}
                      onClick={() => setSelectedMarker(report)}
                      icon={{
                        url: `https://maps.google.com/mapfiles/ms/icons/${
                          report.crimeType === "Theft"
                            ? "yellow"
                            : report.crimeType === "Assault"
                            ? "red"
                            : report.crimeType === "Burglary"
                            ? "pink"
                            : report.crimeType === "Vandalism"
                            ? "blue"
                            : report.crimeType === "Fraud"
                            ? "green"
                            : "purple"
                        }-dot.png`
                      }}
                    />
                  ))}
                  {selectedMarker && (
                    <InfoWindow
                      position={{ lat: selectedMarker.latitude, lng: selectedMarker.longitude }}
                      onCloseClick={() => setSelectedMarker(null)}
                    >
                      <div className="p-2 min-w-[200px]">
                        <span className={`inline-block px-2 py-0.5 rounded-full text-xs font-bold mb-1 ${
                          selectedMarker.crimeType === "Theft"
                            ? "bg-yellow-200 text-yellow-800"
                            : selectedMarker.crimeType === "Assault"
                            ? "bg-red-200 text-red-800"
                            : selectedMarker.crimeType === "Burglary"
                            ? "bg-pink-200 text-pink-800"
                            : selectedMarker.crimeType === "Vandalism"
                            ? "bg-blue-200 text-blue-800"
                            : selectedMarker.crimeType === "Fraud"
                            ? "bg-green-200 text-green-800"
                            : "bg-gray-200 text-gray-800"
                        }`}>
                          {selectedMarker.crimeType}
                        </span>
                        <h3 className="font-bold text-sm">{selectedMarker.description}</h3>
                        <p className="text-xs text-gray-500">
                          {new Date(selectedMarker.timestamp).toLocaleString()}
                        </p>
                      </div>
                    </InfoWindow>
                  )}
                </GoogleMap>
              ) : (
                <div className="w-full h-full flex items-center justify-center">
                  <div className="text-lg text-gray-600">Loading map...</div>
                </div>
              )}
            </div>
          </div>
          <div className="w-72 p-2 flex flex-col gap-2">
            <div className="bg-white/80 rounded-lg shadow-sm p-3">
              <h3 className="text-sm font-semibold text-indigo-900 mb-2 flex items-center gap-1.5">
                <ExclamationTriangleIcon className="h-4 w-4 text-red-500" /> Legend
              </h3>
              <div className="space-y-1.5">
                <div className="flex items-center gap-2">
                  <span className="w-2.5 h-2.5 bg-red-500 rounded-full"></span>
                  <span className="text-xs text-gray-700">High Risk</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-2.5 h-2.5 bg-orange-500 rounded-full"></span>
                  <span className="text-xs text-gray-700">Medium Risk</span>
                </div>
                <div className="flex items-center gap-2">
                  <span className="w-2.5 h-2.5 bg-green-500 rounded-full"></span>
                  <span className="text-xs text-gray-700">Low Risk</span>
                </div>
              </div>
            </div>
            <div className="bg-white/80 rounded-lg shadow-sm p-3">
              <h3 className="text-sm font-semibold text-indigo-900 mb-2 flex items-center gap-1.5">
                <ShieldCheckIcon className="h-4 w-4 text-green-500" /> Safety Tips
              </h3>
              <ul className="space-y-1 text-xs text-gray-700">
                <li className="flex items-start gap-1">
                  <span className="text-indigo-400 mt-1">•</span>
                  <span>Avoid isolated areas after dark</span>
                </li>
                <li className="flex items-start gap-1">
                  <span className="text-indigo-400 mt-1">•</span>
                  <span>Keep valuables secure and out of sight</span>
                </li>
                <li className="flex items-start gap-1">
                  <span className="text-indigo-400 mt-1">•</span>
                  <span>Stay in well-lit areas</span>
                </li>
                <li className="flex items-start gap-1">
                  <span className="text-indigo-400 mt-1">•</span>
                  <span>Be aware of surroundings</span>
                </li>
                <li className="flex items-start gap-1">
                  <span className="text-indigo-400 mt-1">•</span>
                  <span>Report suspicious activities</span>
                </li>
              </ul>
            </div>
            <div className="bg-white/80 rounded-lg shadow-sm p-3 flex-1">
              <h3 className="text-sm font-semibold text-indigo-900 mb-2 flex items-center gap-1.5">
                <MapPinIcon className="h-4 w-4 text-pink-600" /> Recent Reports
              </h3>
              <div className="space-y-2">
                {filteredReports.slice(0, 3).map((report) => (
                  <div key={report.id} className="bg-white/50 rounded-md p-2 text-xs">
                    <div className={`inline-block px-1.5 py-0.5 rounded-full font-medium mb-1 ${
                      report.crimeType === "Theft"
                        ? "bg-yellow-100 text-yellow-800"
                        : report.crimeType === "Assault"
                        ? "bg-red-100 text-red-800"
                        : report.crimeType === "Burglary"
                        ? "bg-pink-100 text-pink-800"
                        : report.crimeType === "Vandalism"
                        ? "bg-blue-100 text-blue-800"
                        : report.crimeType === "Fraud"
                        ? "bg-green-100 text-green-800"
                        : "bg-gray-100 text-gray-800"
                    }`}>
                      {report.crimeType}
                    </div>
                    <p className="text-gray-700 font-medium line-clamp-2">{report.description}</p>
                    <p className="text-[10px] text-gray-500 mt-1">
                      {new Date(report.timestamp).toLocaleString()}
                    </p>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
